def nmoves(x):
	pf = []
	y = x
	for p in primes:
		while x % p == 0:
			pf.append(p)
			x = x // p
		if x == 1 or p > x:
			break
	if x > 1:
		pf.append(x)
	ans = y
	for p in pf:
		y = y // p
		ans += y
	return ans

