from math import factorial, pi
from fractions import Fraction

def comb(n, k):
	return factorial(n) // (factorial(n - k) * factorial(k))

def bernoulli(m):
	b = [1]
	for i in range(1, m + 1):
		n = 0
		for k in range(0, i):
			n += comb(i + 1, k) * b[k]
		b.append(Fraction(-n, i + 1))
	return b
