def series(k, nb):
	if k < 0:
		k = -k
		return b[k + 1] * (-1) ** k / (k + 1)
	elif k % 2 == 1:
		return sum((1 / n ** k for n in range(1, nb + 1)))
	else:
		return abs(b[k]) * (2 * pi) ** k / (2 * factorial(k))
