from fractions import gcd
from collections import Counter
testcases = int(input())

def factorization(n):
	facts = Counter()
	while not n % 2:
		facts.update({2: 1})
		n //= 2
	tryval = 3
	while tryval ** 2 <= n:
		while not n % tryval:
			facts.update({tryval: 1})
			n //= tryval
		tryval += 2
	if n > 1:
		facts.update({n: 1})
	return facts

def totient(n):
	prod = 1
	for (p, exp) in factorization(n).items():
		prod *= (p - 1) * p ** (exp - 1)
	return prod

def multorder(n, m):
	if gcd(n, m) != 1:
		return 0
	order = totient(m)
	fact = factorization(order)
	for p in fact:
		while order % p == 0:
			if pow(n, order // p, m) == 1:
				order //= p
			else:
				break
	return order

def lowest40(baseint):
	count2 = 0
	count5 = 0
	while not baseint % 2:
		baseint //= 2
		count2 += 1
	while not baseint % 5:
		baseint //= 5
		count5 += 1
	return 2 * multorder(10, 9 * baseint) + max(count5, count2 - 2)
