	vis = [0] * 26
	dfs(src, adj, vis)
	for i in range(26):
		if not vis[i] and mark[i] == 1:
			return 0
	return 1

class Solution:

	def isCircle(self, N, A):
		mark = [0] * 26
		adj = [[] for _ in range(26)]
		in_degree = [0] * 26
		out_degree = [0] * 26
		for i in A:
			u = ord(i[0]) - ord('a')
			v = ord(i[-1]) - ord('a')
			adj[u].append(v)
			mark[u] = mark[v] = 1
			in_degree[v] += 1
			out_degree[u] += 1
		for i in range(26):
			if in_degree[i] != out_degree[i]:
				return 0
		return detectCycle(ord(A[0][0]) - ord('a'), adj, mark)
