class Solution:

	def countPairs(self, X, Y, m, n):
		for i in range(m):
			X[i] **= 1 / X[i]
		for i in range(n):
			Y[i] **= 1 / Y[i]
		X.sort()
		Y.sort()
		i = j = 0
		ans = 0
		while i < m:
			if j == n:
				ans += n
			else:
				while j < n and X[i] > Y[j]:
					j += 1
				ans += j
			i += 1
		return ans
