def toposort(graph, indegrees):
	sort = deque()
	zeros = deque()
	for node in indegrees:
		if indegrees[node] == 0:
			zeros.append(node)
	while len(zeros) > 0:
		node = zeros.popleft()
		sort.append(node)
		for to in graph[node]:
			indegrees[to] -= 1
			if indegrees[to] == 0:
				zeros.append(to)
	for node in indegrees:
		if indegrees[node] != 0:
			return None
	return sort

