def update(tree, pos, diff, si):
	pos += si - 1
	while pos:
		tree[pos] += diff
		pos >>= 1

def query(tree, l, r, si):
	(ans, l, r) = (0, l + si - 1, r + si - 1)
	while l < r:
		if l & 1:
			ans += tree[l]
			l += 1
		if not r & 1:
			ans += tree[r]
			r -= 1
		(l, r) = (l >> 1, r >> 1)
	return ans + (0 if l != r else tree[l])

