def modmatrixpower(a, n, mod):
	magic = 2
	for m in [2, 3, 5, 7]:
		if n % m == 0:
			magic = m
			break
	r = None
	if n < magic:
		r = a
		n -= 1
	else:
		s = modmatrixpower(a, n // magic, mod)
		r = s
		for i in range(magic - 1):
			r = modmatrixproduct(r, s, mod)
	for i in range(n % magic):
		r = modmatrixproduct(r, a, mod)
	return r

