def infixToPostfix(exp):


	s = Stack()


	postfix = ""


	for c in exp:


		if (c.isalpha()):
			postfix += c


		elif (c == "+" or c == "-" or c == "*"):


			if s.isempty():
				s.push(c)


			else:



				while s.isempty() == False and precedence(c) <= precedence(s.top()):
					postfix = postfix + s.pop()


				s.push(c)


		elif c == "(":
			s.push(c)
		elif c == ")":



			while s.top() != "(":
				postfix = postfix + s.pop()


			s.pop()


	while s.isempty() == False:
		postfix = postfix + s.pop()

	return postfix


