class Solution:

	def powerfullInteger(self, n: int, intervals: List[List[int]], k: int) -> int:
		counts = Counter()
		for (start, end) in intervals:
			counts[start] += 1
			counts[end + 1] -= 1
		current_count = 0
		powerfull_integer = -1
		for i in sorted(counts):
