def special(l):
	n = len(l)
	for i in range(n):
		lis = [i + 1]
		yield lis
		for j in range(i + 1, n):
			p = l[j] / l[i]
			if p <= 1 or int(p) != p:
				continue
			lis = [i + 1, j + 1]
			yield lis
			sk = (j + 1) * int(p)
			while sk <= n:
				lis.append(sk)
				sk *= int(p)
				yield lis

