class DisjointSet:

	def __init__(self, n, cost):
		self.parent = [i for i in range(n + 1)]
		self.rank = [1] * (n + 1)
		self.cost = cost
		self.visited = [0] * (n + 1)

	def find(self, x):
		if self.parent[x] != x:
			self.parent[x] = self.find(self.parent[x])
		return self.parent[x]

	def union(self, x, y):
		x_repr = self.find(x)
		y_repr = self.find(y)
		if x_repr == y_repr:
			return
		elif self.rank[x_repr] < self.rank[y_repr]:
			self.parent[x_repr] = y_repr
		elif self.rank[y_repr] < self.rank[x_repr]:
			self.parent[y_repr] = x_repr
		else:
			self.parent[x_repr] = y_repr
			self.rank[y_repr] += 1

	def populate_representatives(self, x):
		root = self.find(x)
		if 0 <= self.cost[x] < self.cost[root] or (self.cost[root] < 0 and self.cost[x] >= 0):
			self.cost[root] = self.cost[x]
