class Solution:

	def dfs(self, node, visited, adj):
		visited[node] = 1
		for nbr in adj[node]:
			if visited[nbr] == -1:
				self.dfs(nbr, visited, adj)

	def is_connected(self, V, adj):
		visited = [-1] * V
		cnt = 0
		for i in range(V):
			if visited[i] == -1:
				self.dfs(i, visited, adj)
				cnt += 1
		return cnt == 1

	def RevDelMST(self, arr, V, E):
		n = len(arr)
		adj = defaultdict(list)
		edges = []
		for i in range(0, n, 3):
			(u, v, w) = (arr[i], arr[i + 1], arr[i + 2])
			adj[u].append(v)
			adj[v].append(u)
			edges.append([u, v, w])
		edges.sort(key=lambda x: -x[2])
		cost = 0
		for (u, v, w) in edges:
			adj[u].remove(v)
			adj[v].remove(u)
			if not self.is_connected(V, adj):
				adj[u].append(v)
				adj[v].append(u)
				cost += w
		return cost
