class BIT:

	def __init__(self, n):
		self.n = n
		self.bit = [0] * (n + 1)

	def get_val(self, i):
		i = i + 1
		s = 0
		while i <= self.n:
			s += self.bit[i]
			i += i & -i
		return s

	def _add(self, i, val):
		while i > 0:
			self.bit[i] += val
			i -= i & -i

	def add(self, i, j, val):
		self._add(j, val)
		self._add(i, -val)
