def eular_tour(tree: list, root: int):
	n = len(tree)
	res = []
	begin = [-1] * n
	end = [-1] * n
	visited = [False] * n
	visited[root] = True
	q = deque([root])
	while q:
		pos = q.pop()
		res.append(pos)
		end[pos] = len(res)
		if begin[pos] == -1:
			begin[pos] = len(res) - 1
		for next_pos in tree[pos]:
			if visited[next_pos]:
				continue
			else:
				visited[next_pos] = True
				q.append(pos)
				q.append(next_pos)
	return (res, begin, end)
