import heapq
INF = int(1e+18)
MOD = (1 << 64) - 1

class MinHeap:

	def __init__(self):
		self.h = []

	def push(self, x):
		heapq.heappush(self.h, x)

	def pop(self):
		return heapq.heappop(self.h)

	def isEmpty(self):
		return len(self.h) == 0

def add(x, y):
	return x + y - MOD if x + y >= MOD else x + y

def mul(x, y):
	return x * y % MOD

def go(adj, src):
	n = len(adj)
	frontier = MinHeap()
	ways = [0] * n
	cost = [INF] * n
	frontier.push((0, src))
	ways[src] = 1
	cost[src] = 0
	while not frontier.isEmpty():
		(c, u) = frontier.pop()
		if c > cost[u]:
			continue
		for (v, w) in adj[u]:
			if c + w < cost[v]:
				ways[v] = ways[u]
				cost[v] = c + w
				frontier.push((c + w, v))
			elif c + w == cost[v]:
				ways[v] = add(ways[v], ways[u])
	return (cost, ways)
