	(u, v, w) = map(int, input().split())
	u -= 1
	v -= 1
	edges.append((u, v, w))
	adj[u].append((v, w))
	adj_transpose[v].append((u, w))
(cost_s, ways_s) = go(adj, s)
(cost_t, ways_t) = go(adj_transpose, t)
shortest_path = cost_s[t]
for i in range(m):
	(u, v, w) = edges[i]
	cost = cost_s[u] + cost_t[v] + w
	if cost > shortest_path:
		fix_cost = cost - shortest_path + 1
		w -= cost - shortest_path + 1
		print(f'CAN {fix_cost}' if w >= 1 else 'NO')
	elif ways_s[t] == mul(ways_s[u], ways_t[v]):
		print('YES')
	else:
		print('CAN 1' if w > 1 else 'NO')
