(P, A, S, M, D) = (1, 2, 4, 8, 16)
ADD = A | S
ANY = A | S | M | D
OP_BIT = {'+': A, '-': S, '*': M, '/': D, ')': 0}
UNSAFE_BEFORE = {P: 0, A: 0, S: ADD, M: ADD, D: ANY}
UNSAFE_AFTER = {P: 0, A: 0, S: 0, M: ADD, D: ADD}
macro = dict()

def expr(chars, pseudo=P, expand=macro.get, op_bit=OP_BIT, unsafe_before=UNSAFE_BEFORE, unsafe_after=UNSAFE_AFTER):
	paren = []
	push = paren.append
	pop = paren.pop
	name = []
	keep = name.append
	forget = name.clear
	last_op = retval = naked = pseudo

	def safe():
		nonlocal naked
		naked = expand(''.join(name), pseudo)
		forget()
		if naked <= pseudo:
			return naked
		return not unsafe_before[last_op] & naked
	for c in chars:
		if c == '(':
			push(last_op)
			last_op = pseudo
		elif c not in op_bit:
			keep(c)
		elif not safe():
			return 0
		elif c == ')':
			last_op = pop()
		else:
			last_op = op_bit[c]
			if unsafe_after[last_op] & naked:
				return 0
			if not paren:
				retval |= last_op
	if name and (not safe()):
		return 0
	return retval

def main():
	global macro

	def chars(b, t):
		for k in range(b, len(t)):
			yield from t[k]
	for _ in range(int(input())):
		token = input().split()
		s = token[0] == '
		macro[token[1 + s]] = expr(chars(2 + s, token))
	print('OK' if expr((c for c in input() if c != ' ')) else 'Suspicious')
