class Team(object):

	def __init__(self, id):
		self.id = id
		self.correct = 0
		self.time = 0
		self.penalty = 0
		self.status = {}

	def judge(self, m, p, j):
		if j == 0:
			self.correct += 1
			self.time += m
			if p in self.status:
				self.penalty += 20 * self.status[p]
				del self.status[p]
		elif p in self.status:
			self.status[p] += 1
		else:
			self.status[p] = 1

def calc_result(scores):
	result = []
	for (i, s) in enumerate(scores[1:], start=1):
		if s is not None:
			result.append([-s.correct, s.time + s.penalty, s.id * -1])
		else:
			result.append([0, 0, -i])
	result.sort()
	return result

