class Solution:

	def gcd(self, a, b):
		if b == 0:
			return a
		return self.gcd(b, a % b)

	def solve(self, to, frmJug, target):
		st = frmJug
		toJug = 0
		count = 1
		while st != target and toJug != target:
			temp = min(st, to - toJug)
			toJug += temp
			st -= temp
			count += 1
			if st == target or toJug == target:
				break
			if st == 0:
				st = frmJug
				count += 1
			if toJug == to:
				toJug = 0
				count += 1
		return count

	def minSteps(self, n, m, d):
		if m > n:
			(m, n) = (n, m)
		if d % self.gcd(m, n) != 0:
			return -1
		if m == d or n == d:
			return 1
		if d > n:
			return -1
		op1 = self.solve(m, n, d)
		op2 = self.solve(n, m, d)
		return min(op1, op2)
