class SWAG_Stack:

	def __init__(self, F):
		self.stack1 = deque()
		self.stack2 = deque()
		self.F = F
		self.len = 0

	def push(self, x):
		if self.stack2:
			self.stack2.append((x, self.F(self.stack2[-1][1], x)))
		else:
			self.stack2.append((x, x))
		self.len += 1

	def pop(self):
		if not self.stack1:
			while self.stack2:
				(x, _) = self.stack2.pop()
				if self.stack1:
					self.stack1.appendleft((x, self.F(x, self.stack1[0][1])))
				else:
					self.stack1.appendleft((x, x))
		(x, _) = self.stack1.popleft()
		self.len -= 1
		return x

	def sum_all(self):
		if self.stack1 and self.stack2:
			return self.F(self.stack1[0][1], self.stack2[-1][1])
		elif self.stack1:
			return self.stack1[0][1]
		elif self.stack2:
			return self.stack2[-1][1]
		else:
			return float('inf')
