class Solution:

	def countWays(self, n, Sum):
		if Sum == 0:
			if n == 1:
				return 1
			return -1
		if n * 9 < Sum:
			return -1
		dp = {}

		def rec(d, s):
			if d == 0:
				if s == 0:
					return 1
				return 0
			if (d, s) in dp:
				return dp[d, s]
			su = 0
			for i in range(10):
				su += rec(d - 1, s - i)
			dp[d, s] = su % 1000000007
			return su % 1000000007
		sum1 = 0
		for i in range(1, 10):
			sum1 = (sum1 + rec(n - 1, Sum - i)) % 1000000007
		return sum1
