    def openLock(self, deadends: List[str], target: str) -> int:
        beg = (0, 0, 0, 0)
        target = tuple(int(d) for d in target)
        paths = [(beg, 0)]
        visited = {beg}
        for end in deadends:
            end = tuple(int(d) for d in end)
            if beg == end:
                return -1
            visited.add(end)
        i = 0
        while i < len(paths):
            curr, steps = paths[i]
            if curr == target:
                return steps
