def egcd(a, b):
	if a == 0:
		return (b, 0, 1)
	else:
		(g, y, x) = egcd(b % a, a)
		return (g, x - b // a * y, y)

def modinv(a, m):
	while a < 0:
		a += m
	(g, x, y) = egcd(a, m)
	if g != 1:
		raise Exception('Modular inverse does not exist')
	else:
		return x % m

def choose(n, k):
	r = fact[n] * modinv(fact[k], m) % m
	return r * modinv(fact[n - k], m) % m

def C(n):
	return fact[2 * n] * modinv(fact[n + 1], m) % m
