	def recur(i, arr, n, m, su):
		if i == n:
			if su == 0:
				return True
			else:
				return False
		if dp[i][su] != -1:
			return dp[i][su]
		val1 = recur(i + 1, arr, n, m, (su + arr[i]) % m)
		val2 = recur(i + 1, arr, n, m, (su - arr[i]) % m)
		dp[i][su] = val1 or val2
		return dp[i][su]
	return recur(0, arr, n, m, 0)
