def graphColoring(graph, k, V):

	def issafe(i, graph, color, j, V):
		for k in range(V):
			if graph[i][k] == 1 and color[k] == j:
				return False
		return True

	def helper(i, graph, color, k, V):
		if i == V:
			return True
		for j in range(0, k + 0):
			if issafe(i, graph, color, j, V):
				color[i] = j
				if helper(i + 1, graph, color, k, V):
					return True
				else:
					color[i] = -1
		return False
	color = [-1] * V
