def solveSudoku(board):
    if not findUnassignedLocation(board):
        return True

    for num in range(1, 10):
        if isSafe(board, row, col, num):
            board[row][col] = num

            if solveSudoku(board): 
                return True
            board[row][col] = 0
    return False

def findUnassignedLocation(board):
    for i in range(9):
        for j in range(9):
            if board[i][j] == 0:
                return (i, j)
    return None

def isSafe(board, row, col, num):

    for i in range(9):
        if board[row][i] == num:
            return False


    for i in range(9):
        if board[i][col] == num:
            return False


    row_start = (row // 3) * 3
    col_start = (col // 3) * 3
    for i in range(row_start, row_start + 3):
        for j in range(col_start, col_start + 3):
            if board[i][j] == num:
                return False
    return True </s>

