	import math

	def intersectionArea(self, X1, Y1, R1, X2, Y2, R2):
		D = math.sqrt((X2 - X1) ** 2 + (Y2 - Y1) ** 2)
		if D > R1 + R2:
			return 0
		elif D < abs(R1 - R2):
			return min(R1, R2) ** 2 * 3.14
