class Solution:

	def maximizeTheCuts(self, n, x, y, z):
		dp = [-1] * (n + 1)
		dp[0] = 0
		for i in range(n):
			if dp[i] == -1:
				continue
			if i + x <= n:
				dp[i + x] = max(dp[i + x], 1 + dp[i])
			if i + y <= n:
				dp[i + y] = max(dp[i + y], 1 + dp[i])
			if i + z <= n:
				dp[i + z] = max(dp[i + z], 1 + dp[i])
		return dp[n] if dp[n] != -1 else 0
