def combisum2(i, target, subs, arr, ans, n, num, k):
	if i == n:
		if target == 0 and k == num:
			ans.append(subs[:])
		return
	if target - arr[i] >= 0:
		subs.append(arr[i])
		num += 1
		combisum2(i + 1, target - arr[i], subs, arr, ans, n, num, k)
		num -= 1
		subs.pop()
	while i < n - 1 and arr[i] == arr[i + 1]:
		i += 1
	combisum2(i + 1, target, subs, arr, ans, n, num, k)

