class SegmentTree:

	def __init__(self, arr):
		self.arr = arr
		self.n = len(arr)
		self.seg = [0] * self.n * 4

		def build(ind, low, high):
			if low == high:
				self.seg[ind] = arr[low]
				return
			mid = low + high >> 1
			build(ind * 2 + 1, low, mid)
			build(ind * 2 + 2, mid + 1, high)
			self.seg[ind] = self.seg[ind * 2 + 1] ^ self.seg[ind * 2 + 2]
		build(0, 0, self.n - 1)

	def query(self, ind, low, high, l, r):
		if low > r or l > high:
			return 0
		if low >= l and high <= r:
			return self.seg[ind]
		mid = low + high >> 1
		left = self.query(ind * 2 + 1, low, mid, l, r)
		right = self.query(ind * 2 + 2, mid + 1, high, l, r)
		return left ^ right

class Solution:

	def specialXor(self, N, Q, a, query):
		s = SegmentTree(a)
		ans = 0
		for i in a:
			ans ^= i
		res = []
		for (i, j) in query:
			temp = s.query(0, 0, N - 1, i - 1, j - 1)
			res.append(ans ^ temp)
		return res
