def next_bigger(n):
	s = list(str(n))
	for i in range(len(s) - 2, -1, -1):
		if s[i] < s[i + 1]:
			t = s[i:]
			m = min([x for x in t if x > t[0]])
			t.remove(m)
			t.sort()
			s[i:] = [m] + t
			return int(''.join(s))
	return -1
