import collections
import sys

class Solver(collections.namedtuple('Solver', ('n', 'm', 'penalties', 'degrees', 'edges'))):

	@classmethod
	def read(cls, fin):
		(n, m) = map(int, fin.readline().split())
		penalties = [0]
		penalties.extend(map(int, fin.readline().split()))
		degrees = [0] * (n + 1)
		edges = []
		for i in range(m):
			(u, v) = map(int, fin.readline().split())
			degrees[u] += 1
			degrees[v] += 1
			edges.append((u, v))
		return cls(n, m, penalties, degrees, edges)

	def delete_one(self):
		min_penalty = 100000
		for (penalty, degree) in zip(self.penalties, self.degrees):
			if degree % 2 == 1:
				min_penalty = min(min_penalty, penalty)
		return min_penalty

	def delete_two(self):
		min_penalty = 100000
		for (u, v) in self.edges:
			if (self.degrees[u] + self.degrees[v]) % 2 == 0:
				min_penalty = min(min_penalty, self.penalties[u] + self.penalties[v])
		return min_penalty

	def solve(self):
		if self.m % 2 == 0:
			return 0
		return min(self.delete_one(), self.delete_two())

