def _kosa(a1, a2, b1, b2):
	(x1, y1) = a1
	(x2, y2) = a2
	(x3, y3) = b1
	(x4, y4) = b2
	tc = (x1 - x2) * (y3 - y1) + (y1 - y2) * (x1 - x3)
	td = (x1 - x2) * (y4 - y1) + (y1 - y2) * (x1 - x4)
	return tc * td < 0

def kosa(a1, a2, b1, b2):
	return _kosa(a1, a2, b1, b2) and _kosa(b1, b2, a1, a2)

