			dis = math.sqrt((cell[i][0] - cell[j][0]) ** 2 + (cell[i][1] - cell[j][1]) ** 2 + (cell[i][2] - cell[j][2]) ** 2)
			if dis < cell[i][3] + cell[j][3]:
				uf.union(i, j)
			else:
