class Solution:

	def minCashFlow(self, n, arr):
		n = len(arr)
		optArr = [[0 for i in range(n)] for j in range(n)]
		graph = [[] for i in range(n)]
		amounts = [0 for i in range(n)]
		for i in range(n):
			for j in range(n):
				if arr[i][j]:
					graph[i].append([j, arr[i][j]])
		for i in range(n):
			curLen = len(graph[i])
			for j in range(curLen):
				amounts[i] -= graph[i][j][1]
				amounts[graph[i][j][0]] += graph[i][j][1]
		for i in range(n):
			maxCredit = 0
			maxDebit = 0
			ci = None
			di = None
			for i in range(len(amounts)):
				if amounts[i] > maxCredit:
					ci = i
					maxCredit = amounts[i]
				if amounts[i] < maxDebit:
					di = i
					maxDebit = amounts[i]
			if maxCredit and maxDebit:
				if maxCredit >= abs(maxDebit):
					optArr[di][ci] = abs(maxDebit)
					amounts[di] = 0
					amounts[ci] -= abs(maxDebit)
				else:
					optArr[di][ci] = maxCredit
					amounts[di] += maxCredit
					amounts[ci] = 0
		return optArr
