class Solution:

	def powerOf(self, n, pow, modValue):
		result = 1
		while pow > 0:
			if pow & 1:
				result = result * n % modValue
			n = n * n % modValue
			pow >>= 1
		return result

	def countGoodNumbers(self, n):
		modValue = 1000000007
		isOdd = n & 1 != 0
		result = self.powerOf(20, n >> 1, modValue)
