from math import gcd, atan, pi, degrees

def cross3(a, b, c):
	return (b[0] - a[0]) * (c[1] - a[1]) - (b[1] - a[1]) * (c[0] - a[0])

def convex_hull(ps):
	qs = []
	N = len(ps)
	for p in ps:
		while len(qs) > 1 and cross3(qs[-1], qs[-2], p) > 0:
			qs.pop()
		qs.append(p)
	t = len(qs)
	for i in range(N - 2, -1, -1):
		p = ps[i]
		while len(qs) > t and cross3(qs[-1], qs[-2], p) > 0:
			qs.pop()
		qs.append(p)
	return qs

def tup_to_key(tup):
	(x, y) = tup
	x += 10 ** 7
	y += 10 ** 7
	x *= 10 ** 8
	return x + y

def key_to_tup(key):
	(x, y) = divmod(key, 10 ** 8)
	x -= 10 ** 7
	y -= 10 ** 7
	return (x, y)

def angle(x1, y1, x2, y2):
	if y1 == y2:
		return pi / 2
	return atan(-(x1 - x2) / (y1 - y2))

def on_line(ps):
	st = set()
	for (p, q) in zip(ps, ps[1:]):
		(px, py) = p
		(qx, qy) = q
		(dx, dy) = (qx - px, qy - py)
		g = gcd(dx, dy)
		dx //= g
		dy //= g
		st.add(tup_to_key((dx, dy)))
	return len(st) == 1
