ok_set = set()

def is_consistent(p, margin):
	nonlocal ok_set
	if margin in ok_set:
		return True
	elif p[:margin] == p[-margin:]:
		ok_set.add(margin)
		return True
	else:
		return False

def powmod(p):
	mod = 10 ** 9 + 7
	pbin = bin(p)[2:][-1::-1]
	result = 26 if pbin[0] == '1' else 1
	tmp = 26
	for bit in pbin[1:]:
		tmp *= tmp
		tmp %= mod
		if bit == '1':
			result *= tmp
			result %= mod
	return result
