import fractions

def primes(n):
	primfac = {}
	d = 2
	while d * d <= n:
		while n % d == 0:
			if d in primfac:
				primfac[d] += 1
			else:
				primfac[d] = 1
			n /= d
		d += 1
	if n > 1:
		if n in primfac:
			primfac[n] += 1
		else:
			primfac[n] = 1
	return primfac

def numf(n):
	prod = 1
	dictx = primes(n)
	for i in dictx:
		prod *= dictx[i] + 1
	return prod - 1

def numeps(n):
	if n % 4 != 0:
		return 0
	else:
		n = int(n / 4)
		prod = 1
		dictx = primes(n)
		for i in dictx:
			prod *= int(dictx[i] / 2 + 1)
		if n ** 0.5 % 1 == 0:
			return prod - 1
		else:
			return prod

def simplify(num, denom):
	gcd = fractions.gcd(num, denom)
	num = int(num / gcd)
	denom = int(denom / gcd)
	return str(num) + '/' + str(denom)

def compute(n):
	if numeps(n) == 0:
		return 0
	return simplify(numeps(n), numf(n))
