import math
from typing import Tuple, TypeVar
INF = float('inf')
IntOrFloat = TypeVar('IntOrFloat', int, float)

def ceil(value: IntOrFloat) -> IntOrFloat:
	if value == INF:
		return INF
	return math.ceil(value)

def break_adjacent_sections(health: Tuple[int]) -> int:
	minimum = INF
	for i in range(n - 1):
		x = max(health[i], health[i + 1])
		y = min(health[i], health[i + 1])
		if 2 * y <= x:
			value = ceil(x / 2)
		else:
			value = ceil((x + y) / 3)
		if value < minimum:
			minimum = value
	return minimum

def break_alternate_sections(health: Tuple[int]) -> int:
	minimum = INF
	for i in range(n - 2):
		value = health[i] + health[i + 2]
		if value < minimum:
			minimum = value
	result = ceil(minimum / 2)
	return result

def break_unrelated_sections(health: Tuple[int]) -> int:
	health = sorted(health)
	result = ceil(health[0] / 2) + ceil(health[1] / 2)
	return result
