def rangetodsum(lo, hi):
	(lod, lom) = divmod(lo, 10)
	(hid, him) = divmod(hi, 10)
	if lod == hid:
		dlo = onedsum(lod)
		dsum = {dlo + i: 1 for i in range(lom, him + 1)}
	else:
		dlo = onedsum(lod)
		dsum = {dlo + i: 1 for i in range(lom, 10)}
		dhi = onedsum(hid)
		for h in range(dhi, dhi + him + 1):
			if h in dsum:
				dsum[h] += 1
			else:
				dsum[h] = 1
		lod += 1
		hid -= 1
		if lod <= hid:
			rsum = rangetodsum(lod, hid)
			for r in rsum:
