class DisjointSet:

	def __init__(self, n):
		self.parent = [i for i in range(n + 1)]
		self.size = [1 for i in range(n + 1)]

	def findUParent(self, node):
		if node == self.parent[node]:
			return node
		self.parent[node] = self.findUParent(self.parent[node])
		return self.parent[node]

	def unionByRank(self, u, v):
		up_u = self.findUParent(u)
		up_v = self.findUParent(v)
		if up_u == up_v:
			return
		if self.rank[up_u] < self.rank[up_v]:
			self.parent[up_u] = up_v
		elif self.rank[up_v] < self.rank[up_u]:
			self.parent[up_v] = up_u
		else:
			self.parent[up_v] = up_u
			self.rank[up_u] += 1
		return

	def unionBySize(self, u, v):
		up_u = self.findUParent(u)
		up_v = self.findUParent(v)
		if up_u == up_v:
			return
		if self.size[up_u] < self.size[up_v]:
			self.parent[up_u] = up_v
			self.size[up_v] += self.size[up_u]
		else:
			self.parent[up_v] = up_u
			self.size[up_u] += self.size[up_v]
		return

class Solution:

	def maxRemove(self, adj, n):
		maxr = 0
		maxc = 0
		for i in range(len(adj)):
			maxr = max(maxr, adj[i][0])
			maxc = max(maxc, adj[i][1])
		ds = DisjointSet(maxr + 1 + maxc + 1)
		for i in range(n):
			row = adj[i][0]
			col = adj[i][1] + maxr + 1
			ds.unionBySize(row, col)
		bosses = 0
		for i in range(len(ds.parent)):
			if ds.findUParent(i) == i and ds.size[i] > 1:
				bosses += 1
		return n - bosses
