memo = {}

def C(n, k, p):
	fact = [1]
	for i in range(1, p):
		fact.append(fact[-1] * i % p)
	if p in memo:
		inv_fact = memo[p]
	else:
		inv = [0, 1]
		for i in range(2, p):
			inv.append(p - p // i * inv[p % i] % p)
		inv_fact = [1]
		for i in range(1, p):
			inv_fact.append(inv_fact[-1] * inv[i] % p)
		memo[p] = inv_fact
	ret = 1
	while n > 0:
		n1 = n % p
		k1 = k % p
		if k1 > n1:
			return 0
		ret = ret * fact[n1] * inv_fact[k1] * inv_fact[n1 - k1] % p
		n //= p
		k //= p
	return ret
